{"changed":true,"filter":false,"title":"users_controller.rb","tooltip":"/app/controllers/users_controller.rb","value":"class UsersController < ApplicationController\n  def new\n    #provides an @user IVAR for the form helper.\n    @user = User.new\n  end\n\n  def create\n    @user = User.create(user_params)\n    if @user.save\n      flash[:success] = \"You have successfuly signed up\"\n      log_in(@user)\n      redirect_to user_path(@user)\n    else\n      flash[:alert] = \"Sign up failed\"\n      render 'new'\n    end\n  end\n  \n  def show\n    @user = User.find(params[:id])\n    @toys = @user.toys.all\n  end\n\n  def edit\n    @user = User.find(params[:id])\n  end\n\n  def update \n    @user = User.find(params[:id])\n    if @user.update_attributes(user_params)\n      flash[:success] = \"Profile successfuly updated\"\n      redirect_to @user\n    else\n      flash.now[:alert] = \"Something is wrong\"\n      render 'edit'\n    end\n  end\n  \n  def index\n    @user = User.all\n  end\n\n  def destroy\n    User.find(params[:id]).destroy\n    flash[:success] = \"User deleted\"\n    redirect_to root_url\n  end\n  \n  private\n    #setting strong parameters (Required in rails 4)\n    def user_params\n      params.require(:user).permit(:name, :email, :password, :password_confirmation)\n    end\nend\n","undoManager":{"mark":-2,"position":4,"stack":[[{"group":"doc","deltas":[{"start":{"row":30,"column":53},"end":{"row":31,"column":0},"action":"insert","lines":["",""]},{"start":{"row":31,"column":0},"end":{"row":31,"column":6},"action":"insert","lines":["      "]}]}],[{"group":"doc","deltas":[{"start":{"row":31,"column":4},"end":{"row":31,"column":6},"action":"remove","lines":["  "]}]}],[{"group":"doc","deltas":[{"start":{"row":31,"column":2},"end":{"row":31,"column":4},"action":"remove","lines":["  "]}]}],[{"group":"doc","deltas":[{"start":{"row":31,"column":0},"end":{"row":31,"column":2},"action":"remove","lines":["  "]}]}],[{"group":"doc","deltas":[{"start":{"row":30,"c